import { React, useEffect } from "react";
import { useState } from "react";
import { getTodos, addItem, deleteItem } from "../Api";
import { TextField, CircularProgress, Button } from "@mui/material";

// RIGHT NOW TODOS ARE ONLY BEING UPDATED UPON PAGE RELOAD. FIX THIS LATER
export default function Home() {
  const [todo, setTodo] = useState("");
  const [todos, setTodos] = useState();
  const [success, setSuccess] = useState(false);

  const fetchTodos = async (success) => {
    if (success) {
      const data = await getTodos();
      setTodos(data);
    }
    setSuccess(false);
  };

  useEffect(() => {
    fetchTodos(true);
  }, []);

  // fetch todos on intitial page load:
  useEffect(() => {
    fetchTodos(success);
  }, [success, setSuccess]);

  const addTodo = () => {
    setSuccess(addItem(todo));
  };

  const deleteTodo = (id) => {
    setSuccess(deleteItem(id));
  };

  return (
    <div className="App">
      <div className="todo-content">
        <h1>React Todo App</h1>

        <div className="input-wrapper">
          <TextField
            fullWidth
            type="text"
            name="todo"
            size="small"
            placeholder="Create a new todo"
            onChange={(e) => {
              setTodo(e.target.value);
            }}
          />
          <Button variant="outlined" className="add-button" onClick={addTodo}>
            Add
          </Button>

        </div>
      </div>
    </div>
  );
}
